} else if (x3 < x1 && x0 > x2) {  // three segments descending down-left
   stbtt__handle_clipped_edge(scanline,x,e, x0,y0, x2,y2);
   stbtt__handle_clipped_edge(scanline,x,e, x2,y2, x1,y1);

