static void* intervalCall(void *unused)
{
  while(1){
    //If angle and position data is not updated from prevous data send,
    //data is not sent
    if(!canGetFlag) {
      sleep(1);
      continue;
    }
    canGetFlag = false;

    send_sql();
    sleep(1);
  }

  return nullptr;
}

