bool m_DepthMaskCalled;
GLboolean m_OriginalDepthMask;
inline void glDepthMask(GLboolean flag)
{
    if(!m_DepthMaskCalled) {
        m_DepthMaskCalled = true;
        glGetBooleanv(GL_DEPTH_WRITEMASK, &m_OriginalDepthMask);
    }
    ::glDepthMask(flag);
}

