  {
    columns.push_back(column);
  }
  obj.plid = std::stoi(columns[0]);
  obj.vid = std::stoi(columns[1]);
  obj.length = std::stod(columns[2]);
  obj.dim = std::stod(columns[3]);
  return is;
}

