{
  for (k = 0; k < 3; k++)
  {
    // qd3[txtytzabg][xyz]
    qd3[m + 3][k] = x * (*work) + y * (*(work + 1)) + z * (*(work + 2));
    // x*sc_d[m][k][0] + y*sc_d[m][k][1] + z*sc_d[m][k][2];
    work += 3;
  }
}

/*q?*/
work = (double *)sc_dd;
for (n = 0; n < 3; n++)
{
  for (m = 0; m < 3; m++)
  {
    for (k = 0; k < 3; k++)
    {
      qdd3[n + 3][m + 3][k] = (*work * x + *(work + 1) * y + *(work + 2) * z - qd3[m + 3][k]) / E_THETA;
      work += 3;
    }
  }
}

