    ret[id].aid = std::stoi(tbl[i][1]);
    ret[id].type = std::stoi(tbl[i][2]);
    ret[id].bdid = std::stoi(tbl[i][3]);
    ret[id].linkid = std::stoi(tbl[i][4]);
