template <class Archive> void load (Archive & ar, ORB_SLAM2::KeyFrame &keyframe, const unsigned int version) { ar & keyframe.mnId & keyframe.mnFrameId & keyframe.mTimeStamp & keyframe.mnGridCols & keyframe.mnGridRows & keyframe.mfGridElementWidthInv & keyframe.mfGridElementHeightInv & keyframe.mnTrackReferenceForFrame & keyframe.mnFuseTargetForKF & keyframe.mnBALocalForKF & keyframe.mnBAFixedForKF & keyframe.mnLoopQuery & keyframe.mnLoopWords & keyframe.mLoopScore & keyframe.mnRelocQuery & keyframe.mnRelocWords & keyframe.mRelocScore; ar & keyframe.mTcwGBA & keyframe.mTcwBefGBA & keyframe.mnBAGlobalForKF; ar & keyframe.fx & keyframe.fy & keyframe.cx & keyframe.cy & keyframe.invfx & keyframe.invfy & keyframe.mbf & keyframe.mb & keyframe.mThDepth; ar & keyframe.N; ar & keyframe.mvKeys & keyframe.mvKeysUn & keyframe.mvuRight & keyframe.mvDepth & keyframe.mDescriptors; ar & keyframe.mBowVec & keyframe.mFeatVec; ar & keyframe.mTcp; ar & keyframe.mnScaleLevels & keyframe.mfScaleFactor & keyframe.mfLogScaleFactor & keyframe.mvScaleFactors & keyframe.mvLevelSigma2 & keyframe.mvInvLevelSigma2; ar & keyframe.mnMinX & keyframe.mnMinY & keyframe.mnMaxX & keyframe.mnMaxY & keyframe.mK;
#ifdef MAP_SAVE_IMAGE ar & keyframe.im; #endif
ar & keyframe.Tcw & keyframe.Twc & keyframe.Ow & keyframe.Cw;

  return rail_crossing_.findByKey(key);
}

