    image_raw_ringbuf.push_front(*image_raw_msg);
    //points_raw is empty
    if (points_raw_ringbuf.begin() == points_raw_ringbuf.end()) {
        pthread_mutex_unlock(&mutex);
        ROS_INFO("points_raw ring buffer is empty");
